def max60(dna,maxlength):
	nowhitespace = dna.replace("\n","")
	#print(dna)
	regex = "[A-Z]{1," + str(maxlength) + "}"
	sixty = re.findall(regex,nowhitespace)
	sequence = '\n'.join(sixty)
	#print(sequence)
	return sequence

